    public function ico(): string
    {
        return match ($this) {
            self::Phone         => Blade::render('<x-lucide-phone class="w-6"/>'),
            self::Email         => Blade::render('<x-lucide-mail class="w-6"/>'),
            self::Address       => Blade::render('<x-lucide-map-pin class="w-6"/>'),
        };
    }

    public function getPreLink(): string
    {
        return match ($this) {
            self::Phone         => "phone:",
            self::Email         => "mailto:",
            default             => '',
        };
    }
    public function getInputType(): string
    {
        return match ($this) {
            self::Phone         => "tel",
            self::Email         => "email",
            default             => '',
        };
    }

    public static function getSortedCasesByName()
    {

        $result = [];
        foreach (self::cases() as $case)
            $result[$case->name] = $case->getName();

        sort($result);

        return collect($result);

    }
}
