<script>
    document.addEventListener('DOMContentLoaded',()=>{
        let employeesList = document.querySelector('#employeesList');
        let employeesBoxes = document.querySelectorAll('.emloyeeBox');

        var activeIndex = -1;

        function showCurrentImg(index) {
            if (!employeesBoxes[index]) return;
            if (employeesBoxes[index]) {
                var currentImgBox = employeesBoxes[index].querySelector('.emloyeeImgBox');
                var fioText = employeesBoxes[index].querySelector('h3');
                var jobTitle  = employeesBoxes[index].querySelector('span');
                fioText.classList.toggle('text-[#C10F1a]');
                jobTitle.classList.toggle('text-[#C10F1a]');
                if(index % 2 == 1){
                    currentImgBox.classList.toggle('hidden');
                    if(!currentImgBox.classList.contains('md:left-[calc(-100%-20px)]')){
                        currentImgBox.classList.add('md:left-[calc(-100%-20px)]');
                    }
                    employeesList.classList.add('min-h-[288px]');
                }
                if(index % 2 == 0){
                    currentImgBox.classList.toggle('hidden');
                    if(!currentImgBox.classList.contains('md:right-[calc(-100%-20px)]')){
                        currentImgBox.classList.add('md:right-[calc(-100%-20px)]');
                    }
                    employeesList.classList.add('min-h-[288px]');
                }
            }
        }

        employeesBoxes.forEach((employeeBox,index) => {
            employeeBox.addEventListener('click', () => {

                const isClosing = index === activeIndex;

                employeesBoxes.forEach((imgBox,index) => {
                    imgBox.querySelector('.emloyeeImgBox').classList.add('hidden');
                    var fioText = imgBox.querySelector('h3').classList.remove('text-[#C10F1a]');
                    var jobTitle  = imgBox.querySelector('span').classList.remove('text-[#C10F1a]');
                });
                employeesList.classList.remove('min-h-[288px]');

                activeIndex = -1;

                if (!isClosing) {
                    activeIndex = index;
                    
                    showCurrentImg(activeIndex);
                }
            })
        })
        
    });
</script>